import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o,c,e as d}from"./app.05828f5a.js";const a={},p=d('<p><img src="http://qiniu.chanmufeng.com/2022-06-07-080711.png#alt=image-20220607160711838" alt="" loading="lazy"></p><p>\u8FD9\u662F\u4E00\u4E2A\u5F88\u6709\u610F\u601D\u5374\u5F88\u5C11\u6709\u4EBA\u6CE8\u610F\u7684\u95EE\u9898\u3002</p><p>\u5F53\u6211\u7528Google\u641C\u7D22<code>MySQL</code>\u8FD9\u4E2A\u5173\u952E\u8BCD\u7684\u65F6\u5019\uFF0CGoogle\u53EA\u63D0\u4F9B\u4E86<code>13</code>\u9875\u7684\u641C\u7D22\u7ED3\u679C\uFF0C\u6211\u901A\u8FC7\u4FEE\u6539url\u7684\u5206\u9875\u53C2\u6570\u8BD5\u56FE\u641C\u7D22\u7B2C<code>14</code>\u9875\u6570\u636E\uFF0C\u7ED3\u679C\u51FA\u73B0\u4E86\u4EE5\u4E0B\u7684\u9519\u8BEF\u63D0\u793A\uFF1A</p><p><img src="http://qiniu.chanmufeng.com/2022-06-08-051423.png#alt=Google\u4E0D\u80FD\u65E0\u9650\u5206\u9875" alt="" loading="lazy"></p><p>\u767E\u5EA6\u641C\u7D22\u540C\u6837\u4E0D\u63D0\u4F9B\u65E0\u9650\u5206\u9875\uFF0C\u5BF9\u4E8E<code>MySQL</code>\u5173\u952E\u8BCD\uFF0C\u767E\u5EA6\u641C\u7D22\u63D0\u4F9B\u4E86<code>76</code>\u9875\u7684\u641C\u7D22\u7ED3\u679C\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-06-08-045404.png#alt=\u767E\u5EA6\u4E0D\u80FD\u65E0\u9650\u5206\u9875" alt="" loading="lazy"></p><h2 id="\u4E3A\u4EC0\u4E48\u4E0D\u652F\u6301\u65E0\u9650\u5206\u9875" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u4E0D\u652F\u6301\u65E0\u9650\u5206\u9875" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u4E0D\u652F\u6301\u65E0\u9650\u5206\u9875</h2><p>\u5F3A\u5982Google\u641C\u7D22\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u652F\u6301\u65E0\u9650\u5206\u9875\uFF1F\u65E0\u975E\u6709\u4E24\u79CD\u53EF\u80FD\uFF1A</p><ul><li>\u505A\u4E0D\u5230</li><li>\u6CA1\u5FC5\u8981</li></ul><p>\u300C\u505A\u4E0D\u5230\u300D\u662F\u4E0D\u53EF\u80FD\u7684\uFF0C\u552F\u4E00\u7684\u7406\u7531\u5C31\u662F\u300C\u6CA1\u5FC5\u8981\u300D\u3002</p><p>\u9996\u5148\uFF0C\u5F53\u7B2C1\u9875\u7684\u641C\u7D22\u7ED3\u679C\u6CA1\u6709\u6211\u4EEC\u9700\u8981\u7684\u5185\u5BB9\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u901A\u5E38\u4F1A\u7ACB\u5373\u66F4\u6362\u5173\u952E\u8BCD\uFF0C\u800C\u4E0D\u662F\u7FFB\u7B2C2\u9875\uFF0C\u66F4\u4E0D\u7528\u8BF4\u7FFB\u523010\u9875\u5F80\u540E\u4E86\u3002\u8FD9\u662F\u6CA1\u5FC5\u8981\u7684\u7B2C\u4E00\u4E2A\u7406\u7531\u2014\u2014\u7528\u6237\u9700\u6C42\u4E0D\u5F3A\u70C8\u3002</p><p>\u5176\u6B21\uFF0C\u65E0\u9650\u5206\u9875\u7684\u529F\u80FD\u5BF9\u4E8E\u641C\u7D22\u5F15\u64CE\u800C\u8A00\u662F\u975E\u5E38\u6D88\u8017\u6027\u80FD\u7684\u3002\u4F60\u53EF\u80FD\u611F\u89C9\u5F88\u5947\u602A\uFF0C\u7FFB\u5230\u7B2C2\u9875\u548C\u7FFB\u5230\u7B2C1000\u9875\u4E0D\u90FD\u662F\u641C\u7D22\u561B\uFF0C\u80FD\u6709\u4EC0\u4E48\u533A\u522B\uFF1F</p><p>\u5B9E\u9645\u4E0A\uFF0C\u641C\u7D22\u5F15\u64CE\u9AD8\u53EF\u7528\u548C\u9AD8\u4F38\u7F29\u6027\u7684\u8BBE\u8BA1\u5E26\u6765\u7684\u4E00\u4E2A\u526F\u4F5C\u7528\u5C31\u662F\u65E0\u6CD5\u9AD8\u6548\u5B9E\u73B0\u65E0\u9650\u5206\u9875\u529F\u80FD\uFF0C\u65E0\u6CD5\u9AD8\u6548\u610F\u5473\u7740\u80FD\u5B9E\u73B0\uFF0C\u4F46\u662F\u4EE3\u4EF7\u6BD4\u8F83\u5927\uFF0C\u8FD9\u662F\u6240\u6709\u641C\u7D22\u5F15\u64CE\u90FD\u4F1A\u9762\u4E34\u7684\u4E00\u4E2A\u95EE\u9898\uFF0C\u4E13\u4E1A\u4E0A\u53EB\u505A\u300C<code>\u6DF1\u5EA6\u5206\u9875</code>\u300D\u3002\u8FD9\u4E5F\u662F\u6CA1\u5FC5\u8981\u7684\u7B2C\u4E8C\u4E2A\u7406\u7531\u2014\u2014\u5B9E\u73B0\u6210\u672C\u9AD8\u3002</p><p>\u6211\u81EA\u7136\u4E0D\u77E5\u9053Google\u7684\u641C\u7D22\u5177\u4F53\u662F\u600E\u4E48\u505A\u7684\uFF0C\u56E0\u6B64\u63A5\u4E0B\u6765\u6211\u7528<code>ES</code>\uFF08Elasticsearch\uFF09\u4E3A\u4F8B\u6765\u89E3\u91CA\u4E00\u4E0B\u4E3A\u4EC0\u4E48\u6DF1\u5EA6\u5206\u9875\u5BF9\u641C\u7D22\u5F15\u64CE\u6765\u8BF4\u662F\u4E00\u4E2A\u5934\u75BC\u7684\u95EE\u9898\u3002</p><h2 id="\u4E3A\u4EC0\u4E48\u62FFes\u4E3E\u4F8B\u5B50" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u62FFes\u4E3E\u4F8B\u5B50" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u62FFES\u4E3E\u4F8B\u5B50</h2><p><code>Elasticsearch</code>\uFF08\u4E0B\u6587\u7B80\u79F0<code>ES</code>\uFF09\u5B9E\u73B0\u7684\u529F\u80FD\u548CGoogle\u4EE5\u53CA\u767E\u5EA6\u641C\u7D22\u63D0\u4F9B\u7684\u529F\u80FD\u662F\u76F8\u540C\u7684\uFF0C\u800C\u4E14\u5728\u5B9E\u73B0\u9AD8\u53EF\u7528\u548C\u9AD8\u4F38\u7F29\u6027\u7684\u65B9\u6CD5\u4E0A\u4E5F\u5927\u540C\u5C0F\u5F02\uFF0C\u6DF1\u5EA6\u5206\u9875\u7684\u95EE\u9898\u90FD\u662F\u7531\u8FD9\u4E9B\u5927\u540C\u5C0F\u5F02\u7684\u4F18\u5316\u65B9\u6CD5\u5BFC\u81F4\u7684\u3002</p><h2 id="\u4EC0\u4E48\u662Fes" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662Fes" aria-hidden="true">#</a> \u4EC0\u4E48\u662FES</h2><p><code>ES</code>\u662F\u4E00\u4E2A\u5168\u6587\u641C\u7D22\u5F15\u64CE\u3002</p><p>\u5168\u6587\u641C\u7D22\u5F15\u64CE\u53C8\u662F\u4E2A\u4EC0\u4E48\u9B3C\uFF1F</p><p>\u8BD5\u60F3\u4E00\u4E2A\u573A\u666F\uFF0C\u4F60\u5076\u7136\u542C\u5230\u4E86\u4E00\u9996\u65CB\u5F8B\u7279\u522B\u4F18\u7F8E\u7684\u6B4C\u66F2\uFF0C\u56DE\u5BB6\u4E4B\u540E\u4F9D\u7136\u611F\u89C9\u4F59\u97F3\u7ED5\u6881\uFF0C\u53EF\u662F\u65E0\u5948\u4F60\u53EA\u8BB0\u5F97\u4E00\u53E5\u6B4C\u8BCD\u4E2D\u7684\u51E0\u4E2A\u5B57\uFF1A\u300C<strong>\u4F1E\u7684\u8FB9\u7F18</strong>\u300D\u3002\u8FD9\u65F6\u5019\u641C\u7D22\u5F15\u64CE\u5C31\u53D1\u6325\u4F5C\u7528\u4E86\u3002</p><p>\u4F7F\u7528\u641C\u7D22\u5F15\u64CE\u4F60\u53EF\u4EE5\u83B7\u53D6\u5230\u5E26\u6709\u300C<strong>\u4F1E\u7684\u8FB9\u7F18</strong>\u300D\u5173\u952E\u8BCD\u7684\u6240\u6709\u7ED3\u679C\uFF0C\u8FD9\u4E9B\u7ED3\u679C\u6709\u4E00\u4E2A\u672F\u8BED\uFF0C\u53EB\u505A\u6587\u6863\u3002\u5E76\u4E14\u641C\u7D22\u7ED3\u679C\u662F\u6309\u7167\u6587\u6863\u4E0E\u5173\u952E\u8BCD\u7684\u76F8\u5173\u6027\u8FDB\u884C\u6392\u5E8F\u4E4B\u540E\u8FD4\u56DE\u7684\u3002\u6211\u4EEC\u5F97\u5230\u4E86\u5168\u6587\u641C\u7D22\u5F15\u64CE\u7684\u5B9A\u4E49\uFF1A</p><blockquote><p>\u5168\u6587\u641C\u7D22\u5F15\u64CE\u662F<strong>\u6839\u636E\u6587\u6863\u5185\u5BB9\u67E5\u627E\u76F8\u5173\u6587\u6863</strong>\uFF0C\u5E76\u6309\u7167<strong>\u76F8\u5173\u6027\u987A\u5E8F</strong>\u8FD4\u56DE\u641C\u7D22\u7ED3\u679C\u7684\u4E00\u79CD\u5DE5\u5177</p></blockquote><p><img src="http://qiniu.chanmufeng.com/2022-06-09-064954.png#alt=" alt="" loading="lazy"></p><p>\u7F51\u4E0A\u51B2\u6D6A\u592A\u4E45\uFF0C\u6211\u4EEC\u4F1A\u6E10\u6E10\u5730\u628A\u8BA1\u7B97\u673A\u7684\u80FD\u529B\u8BEF\u4EE5\u4E3A\u662F\u81EA\u5DF1\u672C\u8EAB\u5177\u5907\u7684\u80FD\u529B\uFF0C\u6BD4\u5982\u6211\u4EEC\u53EF\u80FD\u8BEF\u4EE5\u4E3A\u6211\u4EEC\u5927\u8111\u672C\u8EAB\u5C31\u5F88\u64C5\u957F\u8FD9\u79CD\u641C\u7D22\u3002\u6070\u6070\u76F8\u53CD\uFF0C\u5168\u6587\u68C0\u7D22\u7684\u529F\u80FD\u662F\u6211\u4EEC\u975E\u5E38\u4E0D\u64C5\u957F\u7684\u3002</p><p>\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u5982\u679C\u6211\u5BF9\u4F60\u8BF4\uFF1A\u9759\u591C\u601D\u3002\u4F60\u53EF\u80FD\u8131\u53E3\u800C\u51FA\uFF1A\u5E8A\u524D\u660E\u6708\u5149\uFF0C\u7591\u662F\u5730\u4E0A\u971C\u3002\u4E3E\u5934\u671B\u660E\u6708\uFF0C\u4F4E\u5934\u601D\u6545\u4E61\u3002\u4F46\u662F\u5982\u679C\u6211\u8BA9\u4F60\u8BF4\u51FA\u5E26\u6709\u300C\u6708\u300D\u7684\u53E4\u8BD7\uFF0C\u60F3\u5FC5\u4F60\u4F1A\u8D39\u4E0A\u4E00\u756A\u529F\u592B\u3002</p><p>\u5305\u62EC\u6211\u4EEC\u5E73\u65F6\u770B\u7684\u4E66\u4E5F\u662F\u4E00\u6837\uFF0C\u76EE\u5F55\u672C\u8EAB\u5C31\u662F\u4E00\u79CD\u7B26\u5408\u6211\u4EEC\u4EBA\u8111\u68C0\u7D22\u7279\u70B9\u7684\u4E00\u79CD\u641C\u7D22\u7ED3\u6784\uFF0C\u8BA9\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u6587\u6863ID\u6216\u8005\u6587\u6863\u6807\u9898\u8FD9\u79CD\u603B\u9886\u6027\u7684\u6807\u8BC6\u6765\u627E\u5230\u67D0\u4E00\u7BC7\u6587\u6863\uFF0C\u8FD9\u79CD\u7ED3\u6784\u53EB\u505A<code>\u6B63\u6392\u7D22\u5F15</code>\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-06-08-092727.png#alt=\u76EE\u5F55\u5C31\u662F\u6B63\u6392\u7D22\u5F15" alt="" loading="lazy"></p><p>\u800C\u5168\u6587\u641C\u7D22\u5F15\u64CE\u6070\u597D\u76F8\u53CD\uFF0C\u662F\u901A\u8FC7\u6587\u6863\u4E2D\u7684\u5185\u5BB9\u6765\u627E\u5BFB\u6587\u6863\uFF0C\u8BD7\u8BCD\u5927\u4F1A\u4E2D\u7684\u98DE\u82B1\u4EE4\u5C31\u662F\u4EBA\u8111\u7248\u7684\u5168\u6587\u641C\u7D22\u5F15\u64CE\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-06-08-091433.png#alt=\u98DE\u82B1\u4EE4\u5C31\u662F\u5168\u6587\u641C\u7D22" alt="" loading="lazy"></p><p>\u5168\u6587\u641C\u7D22\u5F15\u64CE\u4F9D\u8D56\u7684\u6570\u636E\u7ED3\u6784\u5C31\u662F\u5927\u540D\u9F0E\u9F0E\u7684<code>\u5012\u6392\u7D22\u5F15</code>\uFF08\u300C\u5012\u6392\u300D\u8FD9\u4E2A\u8BCD\u5C31\u8BF4\u660E\u8FD9\u79CD\u6570\u636E\u7ED3\u6784\u548C\u6211\u4EEC\u6B63\u5E38\u7684\u601D\u7EF4\u65B9\u5F0F\u6070\u597D\u76F8\u53CD\uFF09\uFF0C\u5B83\u662F\u5355\u8BCD\u548C\u6587\u6863\u4E4B\u95F4\u5305\u542B\u5173\u7CFB\u7684\u4E00\u79CD\u5177\u4F53\u5B9E\u73B0\u5F62\u5F0F\u3002</p><p><img src="https://qiniu.chanmufeng.com/2022-02-06-073143.png#alt=\u5355\u8BCD\u6587\u6863\u77E9\u9635" alt="" loading="lazy"></p><p>\u6253\u4F4F\uFF01\u4E0D\u80FD\u7EE7\u7EED\u5C55\u5F00\u4E86\u8BDD\u9898\u4E86\uFF0C\u8D76\u7D27\u4E00\u53E5\u8BDD\u4ECB\u7ECD\u5B8CES\u5427\uFF01</p><blockquote><p><code>ES</code>\u662F\u4E00\u6B3E<strong>\u4F7F\u7528\u5012\u6392\u7D22\u5F15\u6570\u636E\u7ED3\u6784\u3001\u80FD\u591F\u6839\u636E\u6587\u6863\u5185\u5BB9\u67E5\u627E\u76F8\u5173\u6587\u6863</strong>\uFF0C\u5E76\u6309\u7167<strong>\u76F8\u5173\u6027\u987A\u5E8F</strong>\u8FD4\u56DE\u641C\u7D22\u7ED3\u679C\u7684\u5168\u6587\u641C\u7D22\u5F15\u64CE</p></blockquote><h2 id="\u9AD8\u53EF\u7528\u7684\u79D8\u5BC6\u2014\u2014\u526F\u672C-replication" tabindex="-1"><a class="header-anchor" href="#\u9AD8\u53EF\u7528\u7684\u79D8\u5BC6\u2014\u2014\u526F\u672C-replication" aria-hidden="true">#</a> \u9AD8\u53EF\u7528\u7684\u79D8\u5BC6\u2014\u2014\u526F\u672C\uFF08Replication\uFF09</h2><p><strong>\u9AD8\u53EF\u7528</strong>\u662F\u4F01\u4E1A\u7EA7\u670D\u52A1\u5FC5\u987B\u8003\u8651\u7684\u4E00\u4E2A\u6307\u6807\uFF0C\u9AD8\u53EF\u7528\u5FC5\u7136\u6D89\u53CA\u5230\u96C6\u7FA4\u548C\u5206\u5E03\u5F0F\uFF0C\u597D\u5728ES\u5929\u7136\u652F\u6301\u96C6\u7FA4\u6A21\u5F0F\uFF0C\u53EF\u4EE5\u975E\u5E38\u7B80\u5355\u5730\u642D\u5EFA\u4E00\u4E2A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u3002</p><p><code>ES</code>\u670D\u52A1\u9AD8\u53EF\u7528\u8981\u6C42\u5176\u4E2D\u4E00\u4E2A\u8282\u70B9\u5982\u679C\u6302\u6389\u4E86\uFF0C\u4E0D\u80FD\u5F71\u54CD\u6B63\u5E38\u7684\u641C\u7D22\u670D\u52A1\u3002\u8FD9\u5C31\u610F\u5473\u7740\u6302\u6389\u7684\u8282\u70B9\u4E0A\u5B58\u50A8\u7684\u6570\u636E\uFF0C\u5FC5\u987B\u5728<strong>\u5176\u4ED6\u8282\u70B9\u4E0A\u7559\u6709\u5B8C\u6574\u7684\u5907\u4EFD</strong>\u3002\u8FD9\u5C31\u662F\u526F\u672C\u7684\u6982\u5FF5\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-06-09-012352.gif#alt=\u526F\u672C" alt="" loading="lazy"></p><p>\u5982\u4E0A\u56FE\u6240\u793A\uFF0C<code>Node1</code>\u4F5C\u4E3A\u4E3B\u8282\u70B9\uFF0C<code>Node2</code>\u548C<code>Node3</code>\u4F5C\u4E3A\u526F\u672C\u8282\u70B9\u4FDD\u5B58\u4E86\u548C\u4E3B\u8282\u70B9\u5B8C\u5168\u76F8\u540C\u7684\u6570\u636E\uFF0C\u8FD9\u6837\u4EFB\u4F55\u4E00\u4E2A\u8282\u70B9\u6302\u6389\u90FD\u4E0D\u4F1A\u5F71\u54CD\u4E1A\u52A1\u7684\u641C\u7D22\u3002\u6EE1\u8DB3\u670D\u52A1\u7684\u9AD8\u53EF\u7528\u8981\u6C42\u3002</p><p>\u4F46\u662F\u6709\u4E00\u4E2A\u81F4\u547D\u7684\u95EE\u9898\uFF0C\u65E0\u6CD5\u5B9E\u73B0\u7CFB\u7EDF\u6269\u5BB9\uFF01\u5373\u4F7F\u6DFB\u52A0\u53E6\u5916\u7684\u8282\u70B9\uFF0C\u5BF9\u6574\u4E2A\u7CFB\u7EDF\u7684\u5BB9\u91CF\u6269\u5145\u4E5F\u8D77\u4E0D\u5230\u4EFB\u4F55\u5E2E\u52A9\u3002\u56E0\u4E3A\u6BCF\u4E00\u4E2A\u8282\u70B9\u90FD\u5B8C\u6574\u4FDD\u5B58\u4E86\u6240\u6709\u7684\u6587\u6863\u6570\u636E\u3002</p><p>\u56E0\u6B64\uFF0C<code>ES</code>\u5F15\u5165\u4E86\u5206\u7247\uFF08<code>Shard</code>\uFF09\u7684\u6982\u5FF5\u3002</p><h2 id="pb\u7EA7\u6570\u91CF\u7684\u57FA\u77F3\u2014\u2014\u5206\u7247-shard" tabindex="-1"><a class="header-anchor" href="#pb\u7EA7\u6570\u91CF\u7684\u57FA\u77F3\u2014\u2014\u5206\u7247-shard" aria-hidden="true">#</a> PB\u7EA7\u6570\u91CF\u7684\u57FA\u77F3\u2014\u2014\u5206\u7247\uFF08Shard\uFF09</h2><p><code>ES</code>\u5C06\u6BCF\u4E2A\u7D22\u5F15\uFF08<code>ES</code>\u4E2D\u4E00\u7CFB\u5217\u6587\u6863\u7684\u96C6\u5408\uFF0C\u76F8\u5F53\u4E8E<code>MySQL</code>\u4E2D\u7684\u8868\uFF09\u5206\u6210\u82E5\u5E72\u4E2A\u5206\u7247\uFF0C\u5206\u7247\u5C06<strong>\u5C3D\u53EF\u80FD\u5E73\u5747\u5730</strong>\u5206\u914D\u5230\u4E0D\u540C\u7684\u8282\u70B9\u4E0A\u3002\u6BD4\u5982\u73B0\u5728\u4E00\u4E2A\u96C6\u7FA4\u4E2D\u67093\u53F0\u8282\u70B9\uFF0C\u7D22\u5F15\u88AB\u5206\u6210\u4E865\u4E2A\u5206\u7247\uFF0C\u5206\u914D\u65B9\u5F0F\u5927\u81F4\uFF08\u56E0\u4E3A\u5177\u4F53\u5982\u4F55\u5E73\u5747\u5206\u914D\u53D6\u51B3\u4E8E<code>ES</code>\uFF09\u5982\u4E0B\u56FE\u6240\u793A\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-06-09-061618.gif#alt=\u5206\u7247" alt="" loading="lazy"></p><p>\u8FD9\u6837\u4E00\u6765\uFF0C\u96C6\u7FA4\u7684\u6A2A\u5411\u6269\u5BB9\u5C31\u975E\u5E38\u7B80\u5355\u4E86\uFF0C\u73B0\u5728\u6211\u4EEC\u5411\u96C6\u7FA4\u4E2D\u518D\u6DFB\u52A02\u4E2A\u8282\u70B9\uFF0C\u5219<code>ES</code>\u4F1A\u81EA\u52A8\u5C06\u5206\u7247\u5747\u8861\u5230\u5404\u4E2A\u8282\u70B9\u4E4B\u4E0A\uFF1A</p><p><img src="http://qiniu.chanmufeng.com/2022-06-09-015750.gif#alt=\u6A2A\u5411\u6269\u5C55" alt="" loading="lazy"></p><h2 id="\u9AD8\u53EF\u7528-\u5F39\u6027\u6269\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u9AD8\u53EF\u7528-\u5F39\u6027\u6269\u5BB9" aria-hidden="true">#</a> \u9AD8\u53EF\u7528 + \u5F39\u6027\u6269\u5BB9</h2><p>\u526F\u672C\u548C\u5206\u7247\u529F\u80FD\u901A\u529B\u534F\u4F5C\u9020\u5C31\u4E86<code>ES</code>\u5982\u4ECA<strong>\u9AD8\u53EF\u7528</strong>\u548C<strong>\u652F\u6301PB\u7EA7\u6570\u636E\u91CF</strong>\u7684\u4E24\u5927\u4F18\u52BF\u3002</p><p>\u73B0\u5728\u6211\u4EEC\u4EE53\u4E2A\u8282\u70B9\u4E3A\u4F8B\uFF0C\u5C55\u793A\u4E00\u4E0B\u5206\u7247\u6570\u91CF\u4E3A<code>5</code>\uFF0C\u526F\u672C\u6570\u91CF\u4E3A<code>1</code>\u7684\u60C5\u51B5\u4E0B\uFF0C<code>ES</code>\u5728\u4E0D\u540C\u8282\u70B9\u4E0A\u7684\u5206\u7247\u6392\u5E03\u60C5\u51B5\uFF1A</p><p><img src="http://qiniu.chanmufeng.com/2022-06-09-021711.png#alt=\u4E3B\u5206\u7247\u548C\u526F\u5206\u7247\u7684\u5206\u5E03" alt="" loading="lazy"></p><p>\u6709\u4E00\u70B9\u9700\u8981\u6CE8\u610F\uFF0C\u4E0A\u56FE\u793A\u4F8B\u4E2D\u4E3B\u5206\u7247\u548C\u5BF9\u5E94\u7684\u526F\u672C\u5206\u7247\u4E0D\u4F1A\u51FA\u73B0\u5728\u540C\u4E00\u4E2A\u8282\u70B9\u4E0A\uFF0C\u81F3\u4E8E\u4E3A\u4EC0\u4E48\uFF0C\u5927\u5BB6\u53EF\u4EE5\u81EA\u5DF1\u601D\u8003\u4E00\u4E0B\u3002</p><h2 id="\u6587\u6863\u7684\u5206\u5E03\u5F0F\u5B58\u50A8" tabindex="-1"><a class="header-anchor" href="#\u6587\u6863\u7684\u5206\u5E03\u5F0F\u5B58\u50A8" aria-hidden="true">#</a> \u6587\u6863\u7684\u5206\u5E03\u5F0F\u5B58\u50A8</h2><p><code>ES</code>\u662F\u600E\u4E48\u786E\u5B9A\u67D0\u4E2A\u6587\u6863\u5E94\u8BE5\u5B58\u50A8\u5230\u54EA\u4E00\u4E2A\u5206\u7247\u4E0A\u5462\uFF1F</p><p><img src="http://qiniu.chanmufeng.com/2022-06-09-061238.png#alt=" alt="" loading="lazy"></p><p>\u901A\u8FC7\u4E0A\u9762\u7684\u6620\u5C04\u7B97\u6CD5\uFF0C<code>ES</code>\u5C06\u6587\u6863\u6570\u636E\u5747\u5300\u5730\u5206\u6563\u5728\u5404\u4E2A\u5206\u7247\u4E2D\uFF0C\u5176\u4E2D<code>routing</code>\u9ED8\u8BA4\u662F\u6587\u6863id\u3002</p><p>\u6B64\u5916\uFF0C\u526F\u672C\u5206\u7247\u7684\u5185\u5BB9\u4F9D\u8D56\u4E3B\u5206\u7247\u8FDB\u884C\u540C\u6B65\uFF0C\u526F\u672C\u5206\u7247\u5B58\u5728\u610F\u4E49\u5C31\u662F\u8D1F\u8F7D\u5747\u8861\u3001\u9876\u4E0A\u968F\u65F6\u53EF\u80FD\u6302\u6389\u7684\u4E3B\u5206\u7247\u4F4D\u7F6E\uFF0C\u6210\u4E3A\u65B0\u7684\u4E3B\u5206\u7247\u3002</p><p>\u73B0\u5728\u57FA\u7840\u77E5\u8BC6\u8BB2\u5B8C\u4E86\uFF0C\u7EC8\u4E8E\u53EF\u4EE5\u8FDB\u884C\u641C\u7D22\u4E86\u3002</p><h2 id="es\u7684\u641C\u7D22\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#es\u7684\u641C\u7D22\u673A\u5236" aria-hidden="true">#</a> ES\u7684\u641C\u7D22\u673A\u5236</h2><p>\u4E00\u56FE\u80DC\u5343\u8A00\uFF1A</p><p><img src="http://qiniu.chanmufeng.com/2022-06-09-051155.gif#alt=es\u641C\u7D22" alt="" loading="lazy"></p><ol><li>\u5BA2\u6237\u7AEF\u8FDB\u884C\u5173\u952E\u8BCD\u641C\u7D22\u65F6\uFF0C<code>ES</code>\u4F1A\u4F7F\u7528\u8D1F\u8F7D\u5747\u8861\u7B56\u7565\u9009\u62E9\u4E00\u4E2A\u8282\u70B9\u4F5C\u4E3A\u534F\u8C03\u8282\u70B9\uFF08<code>Coordinating Node</code>\uFF09\u63A5\u53D7\u8BF7\u6C42\uFF0C\u8FD9\u91CC\u5047\u8BBE\u9009\u62E9\u7684\u662F<code>Node3</code>\u8282\u70B9\uFF1B</li><li><code>Node3</code>\u8282\u70B9\u4F1A\u572810\u4E2A\u4E3B\u526F\u5206\u7247\u4E2D\u968F\u673A\u9009\u62E95\u4E2A\u5206\u7247\uFF08\u6240\u6709\u5206\u7247\u5FC5\u987B\u80FD\u5305\u542B\u6240\u6709\u5185\u5BB9\uFF0C\u4E14\u4E0D\u80FD\u91CD\u590D\uFF09\uFF0C\u53D1\u9001search request\uFF1B</li><li>\u88AB\u9009\u4E2D\u76845\u4E2A\u5206\u7247\u5206\u522B\u6267\u884C\u67E5\u8BE2\u5E76\u8FDB\u884C\u6392\u5E8F\u4E4B\u540E\u8FD4\u56DE\u7ED3\u679C\u7ED9<code>Node3</code>\u8282\u70B9\uFF1B</li><li><code>Node3</code>\u8282\u70B9\u6574\u54085\u4E2A\u5206\u7247\u8FD4\u56DE\u7684\u7ED3\u679C\uFF0C\u518D\u6B21\u6392\u5E8F\u4E4B\u540E\u53D6\u5230\u5BF9\u5E94\u5206\u9875\u7684\u7ED3\u679C\u96C6\u8FD4\u56DE\u7ED9\u5BA2\u6237\u7AEF\u3002</li></ol><blockquote><p>\u6CE8\uFF1A\u5B9E\u9645\u4E0A<code>ES</code>\u7684\u641C\u7D22\u5206\u4E3A<code>Query\u9636\u6BB5</code>\u548C<code>Fetch\u9636\u6BB5</code>\u4E24\u4E2A\u6B65\u9AA4\uFF0C\u5728<code>Query\u9636\u6BB5</code>\u5404\u4E2A\u5206\u7247\u8FD4\u56DE\u6587\u6863Id\u548C\u6392\u5E8F\u503C\uFF0C<code>Fetch\u9636\u6BB5</code>\u6839\u636E\u6587\u6863Id\u53BB\u5BF9\u5E94\u5206\u7247\u83B7\u53D6\u6587\u6863\u8BE6\u60C5\uFF0C\u4E0A\u9762\u7684\u56FE\u7247\u548C\u6587\u5B57\u8BF4\u660E\u5BF9\u6B64\u8FDB\u884C\u4E86\u7B80\u5316\uFF0C\u8BF7\u6089\u77E5\u3002</p></blockquote><p>\u73B0\u5728\u8003\u8651\u5BA2\u6237\u7AEF\u83B7\u53D6<code>990\uFF5E1000</code>\u7684\u6587\u6863\u65F6\uFF0C<code>ES</code>\u5728\u5206\u7247\u5B58\u50A8\u7684\u60C5\u51B5\u4E0B\u5982\u4F55\u7ED9\u51FA\u6B63\u786E\u7684\u641C\u7D22\u7ED3\u679C\u3002</p><p>\u83B7\u53D6<code>990\uFF5E1000</code>\u7684\u6587\u6863\u65F6\uFF0C<code>ES</code>\u5728\u6BCF\u4E2A\u5206\u7247\u4E0B\u90FD\u9700\u8981\u83B7\u53D6<code>1000</code>\u4E2A\u6587\u6863\uFF0C\u7136\u540E\u7531<code>Coordinating Node</code>\u805A\u5408\u6240\u6709\u5206\u7247\u7684\u7ED3\u679C\uFF0C\u7136\u540E\u8FDB\u884C\u76F8\u5173\u6027\u6392\u5E8F\uFF0C\u6700\u540E\u9009\u51FA\u76F8\u5173\u6027\u987A\u5E8F\u5728<code>990\uFF5E1000</code>\u7684<code>10</code>\u6761\u6587\u6863\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-06-09-054132.png#alt=\u6DF1\u5EA6\u5206\u9875" alt="" loading="lazy"></p><p>\u9875\u6570\u8D8A\u6DF1\uFF0C\u6BCF\u4E2A\u8282\u70B9\u5904\u7406\u7684\u6587\u6863\u4E5F\u5C31\u8D8A\u591A\uFF0C\u5360\u7528\u7684\u5185\u5B58\u4E5F\u5C31\u8D8A\u591A\uFF0C\u8017\u65F6\u4E5F\u5C31\u8D8A\u957F\uFF0C\u8FD9\u4E5F\u5C31\u662F\u4E3A\u4EC0\u4E48\u641C\u7D22\u5F15\u64CE\u5382\u5546\u901A\u5E38\u4E0D\u63D0\u4F9B\u6DF1\u5EA6\u5206\u9875\u7684\u539F\u56E0\u4E86\uFF0C\u4ED6\u4EEC\u6CA1\u5FC5\u8981\u5728\u5BA2\u6237\u9700\u6C42\u4E0D\u5F3A\u70C8\u7684\u529F\u80FD\u4E0A\u6D6A\u8D39\u6027\u80FD\u3002</p><hr><p>\u5B8C\u3002</p>',67),n=[p];function t(i,l){return o(),c("div",null,n)}var g=e(a,[["render",t],["__file","Google\u641C\u7D22\u4E3A\u4EC0\u4E48\u4E0D\u80FD\u65E0\u9650\u5206\u9875\uFF1F.html.vue"]]);export{g as default};
