import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as i,e as o}from"./app.32fab98a.js";const c={},r=o('<p>\u5B66Java\u7684\u7B2C\u4E00\u5929\u6211\u4EEC\u5C31\u77E5\u9053Java\u662F\u4E00\u79CD\u8DE8\u5E73\u53F0\u7684\u8BED\u8A00\uFF0C\u867D\u7136\u5F53\u65F6\u53EF\u80FD\u662F\u61F5\u61F5\u61C2\u61C2\uFF0C\u4F46\u662F\u300C\u4E00\u6B21\u7F16\u8BD1\uFF0C\u5230\u5904\u8FD0\u884C\u300D\u7684\u7279\u6027\u60F3\u5FC5\u662F\u7ED9\u4F17\u591AJavaer\u7559\u4E0B\u4E86\u4E0D\u53EF\u78E8\u706D\u7684\u5370\u8C61\u3002</p><p>\u8FD9\u610F\u5473\u7740\uFF0C\u4F7F\u7528Java\u5F00\u53D1\u51FA\u6765\u7684\u7A0B\u5E8F\u7ECF\u8FC7\u7F16\u8BD1\u540E\uFF0C\u53EF\u4EE5\u5728<code>Windows</code>\u4E0A\u8FD0\u884C\uFF0C\u4E5F\u53EF\u4EE5\u5728<code>Linux</code>\u548C<code>Mac</code>\u4E0A\u8FD0\u884C\uFF1B\u53EF\u4EE5\u5728\u624B\u673A\u4E0A\u8FD0\u884C\uFF0C\u751A\u81F3\u662F\u5728<code>X86</code>CPU\u548C<code>ARM</code>\u7684CPU\u4E0A\u8FD0\u884C\u3002</p><p>\u4E0D\u540C\u7684\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u7279\u522B\u662F\u4E0D\u540C\u7684CPU\u67B6\u6784\uFF0C\u5E95\u5C42\u7684\u6307\u4EE4\u4E5F\u662F\u4E0D\u540C\u7684\u3002\u800CJava\u300C\u4E00\u6B21\u7F16\u5199\uFF0C\u5230\u5904\u8FD0\u884C\u300D\u7684\u7279\u6027\u6B63\u662F\u56E0\u4E3AJava\u7F16\u8BD1\u7684\u5B57\u8282\u7801\u6587\u4EF6\u4E0D\u662F\u76F4\u63A5\u8FD0\u884C\u5728\u5E95\u5C42\u7CFB\u7EDF\u5E73\u53F0\u4E4B\u4E0A\uFF0C\u800C\u662F\u5728Java\u865A\u62DF\u673AJVM\u4E0A\u8FD0\u884C\u7684\uFF0CJVM\u5C4F\u853D\u4E86\u5E95\u5C42\u7CFB\u7EDF\u7684\u4E0D\u540C\uFF0C\u4E3AJava\u5B57\u8282\u7801\u6587\u4EF6\u6784\u9020\u4E86\u4E00\u4E2A\u7EDF\u4E00\u7684\u8FD0\u884C\u73AF\u5883\u3002</p><p>\u5230\u5E95\u4EC0\u4E48\u662FJVM\uFF1F</p><h2 id="_1-jvm\u7684\u5B9A\u4E49" tabindex="-1"><a class="header-anchor" href="#_1-jvm\u7684\u5B9A\u4E49" aria-hidden="true">#</a> 1. JVM\u7684\u5B9A\u4E49</h2><p>\u6211\u4EEC\u5148\u770B\u4E00\u4E0BOracle\u5B98\u65B9\u7ED9\u51FA\u7684JVM\u7684\u5B9A\u4E49</p><blockquote><p>The Java Virtual Machine is the cornerstone of the Java platform. It is the component of the technology responsible for its hardware- and operating system-independence, the small size of its compiled code, and its ability to protect users from malicious programs.</p></blockquote><blockquote><p>Java\u865A\u62DF\u673A\u662FJava\u5E73\u53F0\u7684\u57FA\u77F3\u3002\u5B83\u72EC\u7ACB\u4E8E\u786C\u4EF6\u548C\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u7F16\u8BD1\u4EE3\u7801\u5C0F\uFF0C\u80FD\u591F\u4FDD\u62A4\u7528\u6237\u514D\u53D7\u6076\u610F\u7A0B\u5E8F\u7684\u653B\u51FB\u3002</p></blockquote><blockquote><p>The Java Virtual Machine is an abstract computing machine. Like a real computing machine, it has an instruction set and manipulates various memory areas at run time. It is reasonably common to implement a programming language using a virtual machine</p></blockquote><blockquote><p>Java\u865A\u62DF\u673A\u662F\u4E00\u79CD\u62BD\u8C61\u8BA1\u7B97\u673A\u3002\u50CF\u771F\u6B63\u7684\u8BA1\u7B97\u673A\u4E00\u6837\uFF0C\u5B83\u6709\u4E00\u4E2A\u6307\u4EE4\u96C6\uFF0C\u5E76\u5728\u8FD0\u884C\u65F6\u5904\u7406\u5404\u79CD\u5185\u5B58\u533A\u57DF</p></blockquote><h2 id="_2-jdk-jre-jvm" tabindex="-1"><a class="header-anchor" href="#_2-jdk-jre-jvm" aria-hidden="true">#</a> 2. JDK &amp; JRE &amp; JVM</h2><p><img src="http://qiniu.chanmufeng.com/2022-01-04-080550.png" alt="image-20220104160549940" loading="lazy"></p><p>\u4ECEOracle\u5B98\u65B9\u7ED9\u51FA\u7684Java\u6982\u5FF5\u56FE\u4E2D\u53EF\u4EE5\u770B\u51FA\uFF0CJVM\u5904\u4E8EJava\u5E73\u53F0\u7684\u6700\u5E95\u5C42\uFF0CJava\u5E73\u53F0\u7684\u4E00\u5207\u90FD\u662F\u5EFA\u7ACB\u5728JVM\u7684\u57FA\u7840\u4E4B\u4E0A\u3002JVM\u662FJRE\u7684\u5B50\u96C6\uFF0CJRE\u53C8\u662FJDK\u7684\u5B50\u96C6\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-01-04-090113.png" alt="image-20220104170113409" loading="lazy"></p><p>\u6211\u4EEC\u5E73\u65F6\u63A5\u89E6\u6700\u591A\u7684Java\u8BED\u6CD5\u5C31\u662FJDK\u7684\u6700\u4E0A\u5C42\u7ED3\u6784\uFF0C\u5982\u679C\u53EA\u6EE1\u8DB3\u4E8E\u719F\u7EC3\u4F7F\u7528Java\u7684\u5404\u79CDapi\uFF0C\u90A3\u6211\u4EEC\u53EA\u80FD\u7B97\u662F\u5408\u683C\u7684\u201C\u8C03\u5305\u4FA0\u201D\u3002</p><p>Java \u5F00\u53D1\u8FC7\u7A0B\u4E2D\u4E5F\u53EF\u80FD\u4F1A\u9047\u5230\u5404\u79CD\u95EE\u9898\uFF0C\u4E86\u89E3\u4E86JVM\u7684\u57FA\u672C\u6784\u9020\uFF0C\u5C31\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u4ECE\u539F\u7406\u4E0A\u53BB\u89E3\u51B3\u95EE\u9898\u3002</p><h2 id="_3-jvm\u5230\u5E95\u8BE5\u5B66\u4E9B\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#_3-jvm\u5230\u5E95\u8BE5\u5B66\u4E9B\u4EC0\u4E48" aria-hidden="true">#</a> 3. JVM\u5230\u5E95\u8BE5\u5B66\u4E9B\u4EC0\u4E48</h2><p>\u6362\u53E5\u8BDD\u8BF4\uFF0CJVM\u5230\u5E95\u5E94\u8BE5\u4ECE\u54EA\u91CC\u5F00\u59CB\u5165\u624B\u5B66\u4E60\u5462\uFF1F</p><p>\u4E0B\u56FE\u5C55\u793A\u4E86\u6211\u4EEC\u63A5\u4E0B\u6765\u7684\u7CFB\u5217\u6587\u7AE0\u5C06\u4F1A\u4ECB\u7ECD\u7684\u5185\u5BB9\uFF0C\u56FE\u4E2D\u6211\u653E\u5F03\u4E86\u7EC6\u8282\uFF0C\u5E0C\u671B\u4ECE\u5B8F\u89C2\u4E0A\u80FD\u8BA9\u5927\u5BB6\u5BF9JVM\u7684\u5B66\u4E60\u8DEF\u7EBF\u6709\u6E05\u6670\u7684\u8BA4\u8BC6\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-01-04-084804.png" alt="image-20220104164804570" loading="lazy"></p><h3 id="_3-1-\u6E90\u7801\u5230\u5B57\u8282\u7801" tabindex="-1"><a class="header-anchor" href="#_3-1-\u6E90\u7801\u5230\u5B57\u8282\u7801" aria-hidden="true">#</a> 3.1. \u6E90\u7801\u5230\u5B57\u8282\u7801</h3><p>\u6211\u4EEC\u5C06\u4F1A\u4E86\u89E3\u5230</p><ul><li>\u6E90\u7801\u5230\u5B57\u8282\u7801\u7684\u8FC7\u7A0B\u4E2D\u5230\u5E95\u53D1\u751F\u4E86\u4EC0\u4E48</li><li>.class\u6587\u4EF6\u5185\u5BB9\u5230\u5E95\u6709\u4EC0\u4E48\u5965\u79D8</li></ul><h3 id="_3-2-\u7C7B\u6587\u4EF6\u5230\u865A\u62DF\u673A-\u7C7B\u52A0\u8F7D\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#_3-2-\u7C7B\u6587\u4EF6\u5230\u865A\u62DF\u673A-\u7C7B\u52A0\u8F7D\u673A\u5236" aria-hidden="true">#</a> 3.2. \u7C7B\u6587\u4EF6\u5230\u865A\u62DF\u673A\uFF08\u7C7B\u52A0\u8F7D\u673A\u5236\uFF09</h3><p><img src="http://qiniu.chanmufeng.com/2022-01-04-085755.png" alt="image-20220104165755727" loading="lazy"></p><p>\u6211\u4EEC\u4F1A\u4E86\u89E3\u5230</p><ul><li>\u7C7B\u52A0\u8F7D\u7684\u8FC7\u7A0B\u662F\u4EC0\u4E48</li><li>\u7C7B\u52A0\u8F7D\u5668\u7684\u5206\u7C7B</li></ul><h3 id="_3-3-\u8FD0\u884C\u65F6\u6570\u636E\u533A" tabindex="-1"><a class="header-anchor" href="#_3-3-\u8FD0\u884C\u65F6\u6570\u636E\u533A" aria-hidden="true">#</a> 3.3. \u8FD0\u884C\u65F6\u6570\u636E\u533A</h3><p>\u7C7B\u6587\u4EF6\u88AB\u7C7B\u88C5\u8F7D\u5668\u88C5\u8F7D\u8FDBJVM\u4E4B\u540E\uFF0C\u7C7B\u4E2D\u7684\u5185\u5BB9(\u6BD4\u5982\u53D8\u91CF\uFF0C\u5E38\u91CF\uFF0C\u65B9\u6CD5\uFF0C\u5BF9\u8C61\u7B49\u6570\u636E)\u5FC5\u7136\u6709\u4E2A\u53BB\u5904\uFF0C\u4E5F\u5C31\u662F\u8981\u5B58\u50A8\u8D77\u6765\uFF0C\u5B58\u50A8\u7684\u4F4D\u7F6E\u5BF9\u5E94\u5728JVM\u4E2D\uFF0C\u6211\u4EEC\u79F0\u4E3A\u300C\u8FD0\u884C\u65F6\u6570\u636E\u533A\u300D\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-01-04-090126.png" alt="image-20220104170126378" loading="lazy"></p><p>\u5176\u4E2D\uFF0C\u300C\u65B9\u6CD5\u533A\u300D\u548C\u300C\u5806\u300D\u662F\u6240\u6709\u7EBF\u7A0B\u5171\u4EAB\u7684\u8FD0\u884C\u65F6\u6570\u636E\u533A\uFF0C\u968F\u7740JVM\u7684\u542F\u52A8\u800C\u521B\u5EFA\uFF0C\u4F34\u7740JVM\u7684\u9000\u51FA\u800C\u9500\u6BC1\uFF1B</p><p>\u300C\u7A0B\u5E8F\u8BA1\u6570\u5668\u300D\u548C\u300C\u672C\u5730\u6808\u300D\u4EE5\u53CA\u300C\u865A\u62DF\u673A\u6808\u300D\u662F\u6BCF\u4E2A\u7EBF\u7A0B\u72EC\u4EAB\u7684\u8FD0\u884C\u65F6\u6570\u636E\u533A\uFF0C\u968F\u7740\u7EBF\u7A0B\u7684\u521B\u5EFA\u9000\u51FA\u800C\u521B\u5EFA\u9500\u6BC1\u3002</p><h3 id="_3-4-jvm\u5783\u573E\u56DE\u6536" tabindex="-1"><a class="header-anchor" href="#_3-4-jvm\u5783\u573E\u56DE\u6536" aria-hidden="true">#</a> 3.4. JVM\u5783\u573E\u56DE\u6536</h3><p>Java\u7A0B\u5E8F\u5458\u4E0D\u9700\u8981\u624B\u52A8\u91CA\u653E\u5185\u5B58\u6B63\u662F\u5F97\u76CA\u4E8EJVM\u7684\u5783\u573E\u56DE\u6536\u673A\u5236\uFF0C\u6211\u4EEC\u5C06\u4E86\u89E3\u5230</p><ul><li>\u8FDB\u884C\u5783\u573E\u56DE\u6536\uFF0C\u6211\u4EEC\u5982\u4F55\u786E\u5B9A\u54EA\u4E9B\u5BF9\u8C61\u53EF\u4EE5\u88AB\u6E05\u7406\u5462\uFF1F</li><li>\u786E\u5B9A\u5783\u573E\u4E4B\u540E\uFF0C\u5783\u573E\u56DE\u6536\u65B9\u6CD5\u6709\u54EA\u4E9B\uFF0C\u5404\u6709\u4EC0\u4E48\u4F18\u7F3A\u70B9\uFF1F</li><li>JVM\u7684\u5206\u4EE3\u56DE\u6536\u662F\u600E\u4E48\u56DE\u4E8B\u513F\uFF1F</li><li>JVM\u7684\u5783\u573E\u56DE\u6536\u5668\u6709\u54EA\u4E9B\uFF1F\u5404\u81EA\u7684\u4F18\u7F3A\u70B9\u662F\u4EC0\u4E48\uFF1F\u600E\u4E48\u67E5\u770BJDK\u5F53\u524D\u6B63\u5728\u4F7F\u7528\u7684\u662F\u54EA\u4E00\u6B3E\u56DE\u6536\u5668\uFF1F</li></ul><h3 id="_3-5-jvm\u53C2\u6570" tabindex="-1"><a class="header-anchor" href="#_3-5-jvm\u53C2\u6570" aria-hidden="true">#</a> 3.5. JVM\u53C2\u6570</h3><p>JVM \u6709\u5F88\u591A\u914D\u7F6E\u53C2\u6570\uFF0C\u6BD4\u5982\u9047\u5230 <code>OutOfMemoryError</code>\uFF0C\u6211\u4EEC\u5C31\u77E5\u9053\u662F\u5806\u7A7A\u95F4\u4E0D\u8DB3\u4E86\uFF0C\u53EF\u80FD\u662F JVM \u5206\u914D\u7684\u5185\u5B58\u7A7A\u95F4\u4E0D\u8DB3\u4EE5\u8BA9\u7A0B\u5E8F\u6B63\u5E38\u8FD0\u884C\uFF0C\u8FD9\u65F6\u5019\u6211\u4EEC\u9700\u8981\u901A\u8FC7\u8C03\u6574 <code>-Xmx</code> \u53C2\u6570\u589E\u52A0\u5185\u5B58\u7A7A\u95F4\u3002</p><p>\u5982\u679C\u9047\u5230 <code>StackOverflowError</code>\uFF0C\u53EF\u80FD\u662F\u7EBF\u7A0B\u6808\u7A7A\u95F4\u4E0D\u8DB3\uFF0C\u901A\u5E38\u662F\u56E0\u4E3A\u65B9\u6CD5\u9012\u5F52\u5C42\u7EA7\u6216\u666E\u901A\u65B9\u6CD5\u8C03\u7528\u592A\u591A\uFF0C\u5BFC\u81F4\u6808\u5E27\u6EA2\u51FA\u3002\u6211\u4EEC\u53EF\u4EE5\u5148\u901A\u8FC7\u6808\u5F02\u5E38\u4FE1\u606F\u89C2\u5BDF\u662F\u5426\u5B58\u5728\u9519\u8BEF\u7684\u9012\u5F52\u8C03\u6216\u8005\u5C1D\u8BD5\u8C03\u6574 <code>-Xss</code> \u53C2\u6570\u589E\u52A0\u6808\u7A7A\u95F4\u5927\u5C0F\u3002</p><p>\u518D\u6BD4\u5982\uFF0CJVM\u5728\u6267\u884C\u5B57\u8282\u7801\u6307\u4EE4\u662F\u901A\u8FC7\u89E3\u91CA\u6267\u884C\u7684\u65B9\u5F0F\u8FDB\u884C\u7684\uFF0C\u901A\u5E38\u6267\u884C\u6548\u7387\u6BD4\u8F83\u5DEE\uFF0C\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7<code>-server</code>\u53C2\u6570\u6253\u5F00JIT\u7684C2\u7F16\u8BD1\u5668\u8FDB\u884C\u5373\u65F6\u7F16\u8BD1\u4F18\u5316\u3002</p><p>\u6211\u5C06\u4F1A\u4ECB\u7ECDJVM\u7684\u5E38\u7528\u53C2\u6570\uFF0C\u4EE5\u53CA<code>jps</code>\uFF0C<code>jinfo</code>\uFF0C<code>jstat</code>\uFF0C<code>jstack</code>\uFF0C<code>jmap</code>\uFF0C<code>jconsole</code>\uFF0C<code>jvisualvm</code>\u7B49\u547D\u4EE4\u548C\u5DE5\u5177\u7684\u4F7F\u7528\u3002</p><h3 id="_3-6-gc\u65E5\u5FD7" tabindex="-1"><a class="header-anchor" href="#_3-6-gc\u65E5\u5FD7" aria-hidden="true">#</a> 3.6. GC\u65E5\u5FD7</h3><p>\u6700\u540E\u6211\u4EEC\u4F1A\u501F\u52A9\u65E5\u5FD7\u6587\u4EF6\u5206\u6790\u5DE5\u5177\u6765\u5206\u6790GC\u65E5\u5FD7\u6587\u4EF6\uFF0C\u5305\u62EC</p><ul><li>Parallel GC\u65E5\u5FD7</li><li>CMS\u65E5\u5FD7</li><li>G1\u65E5\u5FD7</li></ul>',43),d=[r];function t(n,l){return e(),i("div",null,d)}var s=a(c,[["render",t],["__file","JVM\u5230\u5E95\u8BE5\u5B66\u4E60\u4E9B\u4EC0\u4E48.html.vue"]]);export{s as default};
