import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o,c as d,e as c}from"./app.8c87227e.js";const t={},p=c(`<h2 id="\u4EC0\u4E48\u662F\u5B57\u8282\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u5B57\u8282\u5E8F" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u5B57\u8282\u5E8F</h2><p>\u5B57\u8282\u662F\u5185\u5B58\u8BFB\u5199\u7684\u6700\u5C0F\u5355\u4F4D\uFF0C\u4E00\u4E2A\u5B57\u8282\u80FD\u591F\u8868\u793A\u7684\u6570\u636E\u8303\u56F4\u662F0\uFF5E255\uFF0C\u4E5F\u5C31\u662F\u8BF4\u5982\u679C\u4F60\u8981\u4FDD\u5B58\u4E00\u4E2A\u5728\u6B64\u8303\u56F4\u533A\u95F4\u7684\u6570\u5B57\uFF0C\u4E00\u4E2A\u5B57\u8282\u8DB3\u77E3\u3002</p><p>\u4F46\u662F\u50CF\u53604\u4E2A\u5B57\u8282\u7684<code>int</code>\uFF0C8\u4E2A\u5B57\u8282\u7684<code>double</code>\uFF0C\u8BE5\u600E\u4E48\u5B58\u50A8\u5462\uFF1F\u591A\u4E2A\u5B57\u8282\u8BE5\u7528\u600E\u6837\u7684\u987A\u5E8F\u6765\u8FDB\u884C\u6392\u5217\uFF1F\u4E3E\u4E2A\u4F8B\u5B50\uFF0C<code>0xb3f4</code>\uFF0C\u6570\u503C\u7684\u9AD8\u4F4D<code>b3</code>\u662F\u5B58\u50A8\u5728\u4E86\u5185\u5B58\u7684\u9AD8\u5730\u5740\u5904\uFF0C\u8FD8\u662F\u5185\u5B58\u7684\u4F4E\u5730\u5740\u5904\u5462\uFF1F</p><p>\u8FD9\u79CD\u5B57\u8282\u7684\u6392\u5217\u987A\u5E8F\u5C31\u53EB\u505A\u5B57\u8282\u5E8F\uFF0C\u5B57\u8282\u5E8F\u6709\u4E24\u79CD\uFF1A</p><ol><li>\u5927\u7AEF\u5B57\u8282\u5E8F</li></ol><p>\u6570\u503C\u7684\u4F4E\u5B57\u8282\u653E\u5728\u5185\u5B58\u7684\u4F4E\u5730\u5740\u5904\uFF0C\u6570\u503C\u7684\u9AD8\u5B57\u8282\u653E\u5728\u5185\u5B58\u7684\u9AD8\u5730\u5740\u3002</p><ol start="2"><li>\u5C0F\u7AEF\u5B57\u8282\u5E8F</li></ol><p>\u6570\u503C\u7684\u4F4E\u5B57\u8282\u653E\u5728\u5185\u5B58\u7684\u9AD8\u5730\u5740\u5904\uFF0C\u6570\u503C\u7684\u9AD8\u5B57\u8282\u653E\u5728\u5185\u5B58\u7684\u4F4E\u5730\u5740\u3002</p><h2 id="\u4E3B\u673A\u5B57\u8282\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u4E3B\u673A\u5B57\u8282\u5E8F" aria-hidden="true">#</a> \u4E3B\u673A\u5B57\u8282\u5E8F</h2><p>\u5F53\u6211\u4EEC\u8C08\u8BBA\u5B57\u8282\u5E8F\uFF0C\u5927\u90E8\u5206\u65F6\u5019\u5BF9\u5E94\u7684\u90FD\u662FCPU\u8BBF\u95EE\u5185\u5B58\u65F6\u7684\u6982\u5FF5\uFF0C\u6BD4\u5982\u5BF9\u4E0B\u56FE\u800C\u8A00\uFF1A</p><p><img src="http://qiniu.chanmufeng.com/2022-10-06-030645.png" alt="image-20221006110645258" loading="lazy"></p><p>\u5185\u5B58\u4F4E\u4F4D\u5B58\u50A8\u7684\u5B57\u8282\u662F<code>0xb3</code>\uFF0C\u9AD8\u4F4D\u5B58\u50A8\u7684\u662F<code>0xf4</code>\uFF0C\u81F3\u4E8ECPU\u8BFB\u53D6\u8FD9\u4E2A2\u5B57\u8282\u6570\u636E\u7684\u65F6\u5019\uFF0C\u662F\u5C06\u5176\u89E3\u91CA\u4E3A<code>0xb3f4</code>\uFF08\u5C0F\u7AEF\uFF09\u8FD8\u662F<code>0xf4b3</code>\uFF08\u5927\u7AEF\uFF09\u5C31\u53D6\u51B3\u4E8ECPU\u91C7\u7528\u7684\u662F\u54EA\u4E00\u79CD\u5B57\u8282\u5E8F\u4E86\u3002</p><p>\u5E38\u7528\u7684CPU\u5B57\u8282\u5E8F\u5982\u4E0B\uFF1A</p><ul><li><p>\u5927\u7AEF\u5B57\u8282\u5E8F\uFF1AIBM\u3001PowerPC</p></li><li><p>\u5C0F\u7AEF\u5B57\u8282\u5E8F\uFF1Ax86</p></li></ul><p>\u8FD9\u79CDCPU\u5B57\u8282\u5E8F\u4E5F\u88AB\u79F0\u4E3A<strong>\u4E3B\u673A\u5B57\u8282\u5E8F\uFF08Host Byte Order\uFF09</strong>\u3002\u7FFB\u8BD1\u4E00\u4E0B\u5C31\u662F\uFF0CIBM\u7684\u4E3B\u673A\u5E8F\u662F\u5927\u7AEF\uFF0Cx86\u7684\u4E3B\u673A\u5E8F\u662F\u5C0F\u7AEF\u3002</p><p>\u95EE\u9898\u6765\u4E86\u3002</p><p>\u4E16\u754C\u4E0A\u7684\u4E3B\u673A\u8FD9\u4E48\u591A\uFF0C\u6BCF\u53F0\u4E3B\u673A\u7684CPU\u7C7B\u578B\u8FD8\u4E0D\u4E00\u6837\uFF0CA\u4E3B\u673A\u6309\u7167A\u7684\u4E3B\u673A\u5B57\u8282\u5E8F\u53D1\u4FE1\u606F\u7ED9B\u4E3B\u673A\uFF0CB\u4E3B\u673A\u5982\u679C\u76F4\u63A5\u6309\u7167B\u7684\u4E3B\u673A\u5B57\u8282\u5E8F\u6765\u89E3\u6790\u4FE1\u606F\uFF0C\u90A3\u4E48\u6781\u6709\u53EF\u80FD\u4F1A\u4EA7\u751F\u9519\u8BEF\u3002</p><p>\u8981\u60F3\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0C\u8FD8\u5FC5\u987B\u5F15\u51FA\u4E00\u4E9B\u5176\u4ED6\u60C5\u51B5\u4E0B\u7684\u5B57\u8282\u5E8F\u3002</p><blockquote><p>\u4E3A\u4E86\u907F\u514D\u5F15\u8D77\u6DF7\u6DC6\uFF0C\u6211\u5728\u8FD9\u91CC\u5F3A\u8C03\u4E00\u4E0B\uFF0C\u5B57\u8282\u5E8F\u5C31\u5206\u4E3A2\u79CD\uFF0C\u5927\u7AEF\u548C\u5C0F\u7AEF\u3002\u800C\u6240\u8C13\u7684\u4E3B\u673A\u5B57\u8282\u5E8F\u4EE5\u53CA\u4E0B\u9762\u6211\u5C06\u63D0\u5230\u7684\u4E24\u79CD\uFF0C\u90FD\u53EA\u662F\u5B57\u8282\u5E8F\u4F5C\u7528\u5728\u4E0D\u540C\u7684\u573A\u666F\u4E2D\u53D6\u5F97\u7279\u5B9A\u540D\u79F0\u7F62\u4E86\u3002</p></blockquote><h2 id="\u6587\u4EF6\u5B58\u50A8\u5B57\u8282\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u6587\u4EF6\u5B58\u50A8\u5B57\u8282\u5E8F" aria-hidden="true">#</a> \u6587\u4EF6\u5B58\u50A8\u5B57\u8282\u5E8F</h2><p>\u987E\u540D\u601D\u4E49\uFF0C\u5C31\u662F\u5B58\u50A8\u6587\u4EF6\u4FE1\u606F\u7684\u65F6\u5019\u7528\u7684\u662F\u5927\u7AEF\u8FD8\u662F\u5C0F\u7AEF\u3002</p><p>bmp\u683C\u5F0F\u7684\u56FE\u7247\u5C5E\u4E8E\u5C0F\u7AEF\u5B57\u8282\u5E8F\uFF0Cjpeg\u683C\u5F0F\u7684\u56FE\u7247\u5C31\u662F\u5927\u7AEF\u5B57\u8282\u5E8F\u3002</p><p>\u8FD9\u91CC\u63D0\u53CA\u6587\u4EF6\u5B58\u50A8\u5B57\u8282\u5E8F\u4E3B\u8981\u662F\u4E3A\u4E86\u8BA9\u5927\u5BB6\u7406\u89E3\u4E00\u4E2A\u4E8B\u5B9E\uFF1A<strong>\u91C7\u7528\u4EC0\u4E48\u5B57\u8282\u5E8F\u5B8C\u5168\u662F\u5F00\u53D1\u8005\u8BBE\u8BA1\u4EA7\u54C1\u65F6\u7684\u4E00\u79CD\u6280\u672F\u9009\u578B\u7F62\u4E86</strong>\u3002\u4F46\u662F\u8FD9\u79CD\u9009\u578B\u4E00\u5B9A\u8981\u6210\u4E3A\u4E00\u79CD\u6807\u51C6\uFF0C\u8BA9\u5176\u4ED6\u5F00\u53D1\u8005\u89E3\u6790\u7684\u65F6\u5019\u660E\u767D\u5E94\u8BE5\u7528\u54EA\u79CD\u5B57\u8282\u5E8F\uFF0C\u5426\u5219jpeg\u7684\u56FE\u7247\u4E5F\u5C31\u6CA1\u529E\u6CD5\u5728\u6240\u6709\u7684\u7535\u8111\u4E0A\u6B63\u5E38\u663E\u793A\u4E86\uFF0C\u4F60\u8BF4\u5BF9\u5417\uFF1F</p><p>\u73B0\u5728\u597D\u4E86\uFF0C\u6240\u6709jpeg\u8F6F\u4EF6\u7684\u5F00\u53D1\u8005\u90FD\u77E5\u9053\u5E94\u8BE5\u7528\u5927\u7AEF\u5B57\u8282\u5E8F\u6765\u4FDD\u5B58jpeg\u56FE\u7247\uFF0C\u4F46\u662F\u5BF9\u4E8E\u4ECE\u5176\u4ED6\u4E3B\u673A\u4F20\u8FC7\u6765\u7684jpeg\u6570\u636E\u6D41\uFF0C\u5F00\u53D1\u8005\u53C8\u600E\u4E48\u77E5\u9053\u8FD9\u4E2A\u6570\u636E\u6D41\u7528\u7684\u662F\u5927\u7AEF\u8FD8\u662F\u5C0F\u7AEF\u5462\uFF1F</p><p>\u8FD9\u5C31\u662F<strong>\u7F51\u7EDC\u5B57\u8282\u5E8F\uFF08Network Byte Order\uFF09</strong> \u7684\u95EE\u9898\u4E86\u3002</p><h2 id="\u7F51\u7EDC\u5B57\u8282\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u7F51\u7EDC\u5B57\u8282\u5E8F" aria-hidden="true">#</a> \u7F51\u7EDC\u5B57\u8282\u5E8F</h2><p>\u6211\u60F3\u4F60\u5E94\u8BE5\u80FD\u60F3\u660E\u767D\u4E86\uFF0C\u7F51\u7EDC\u5B57\u8282\u5E8F\u4E0D\u662F\u5927\u7AEF\u5C31\u662F\u5C0F\u7AEF\uFF0C\u4E0D\u53EF\u80FD\u5B58\u5728\u300C\u53EF\u80FD\u662F\u5927\u7AEF\u4E5F\u53EF\u80FD\u662F\u5C0F\u7AEF\u300D\u8FD9\u79CD\u60C5\u51B5\uFF0C\u5426\u5219\u7F51\u7EDC\u4F20\u9012\u7684\u6D88\u606F\u5168\u90FD\u4E71\u5957\u4E86\uFF01</p><p>TCP/IP\u65E2\u7136\u662F\u4E00\u79CD\u6807\u51C6\uFF0C\u90A3\u6807\u51C6\u81EA\u7136\u5C31\u6709\u81EA\u5DF1\u7684\u5B57\u8282\u5E8F\u89C4\u5B9A\u3002</p><p><strong>TCP/IP\u7F51\u7EDC\u91C7\u7528\u7684\u662F\u5927\u7AEF\uFF0C\u662F\u89C4\u5B9A\u597D\u7684\u4E00\u79CD\u6570\u636E\u8868\u793A\u683C\u5F0F\uFF0C\u5B83\u4E0E\u5177\u4F53\u7684CPU\u7C7B\u578B\u3001\u64CD\u4F5C\u7CFB\u7EDF\u7B49\u65E0\u5173\uFF0C\u4ECE\u800C\u53EF\u4EE5\u4FDD\u8BC1\u6570\u636E\u5728\u4E0D\u540C\u4E3B\u673A\u4E4B\u95F4\u4F20\u8F93\u65F6\u80FD\u591F\u88AB\u6B63\u786E\u89E3\u91CA\u3002</strong></p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5982\u679C\u4F60\u60F3\u8BA9\u4F60\u7684\u6D88\u606F\u80FD\u5728\u5176\u4ED6\u8BBE\u5907\u6B63\u5E38\u89E3\u6790\uFF0C\u5C31\u5FC5\u987B\u6309\u7167\u5927\u7AEF\u5B57\u8282\u5E8F\u8FDB\u884C\u5904\u7406\u3002\u6BD4\u5982\uFF0C\u7F51\u7EDC\u4F20\u8F93\u7684\u662F<code>0x12345678</code>\u8FD9\u4E2A\u6574\u5F62\u53D8\u91CF\uFF0C\u9996\u5148\u88AB\u53D1\u9001\u7684\u5E94\u8BE5\u662F<code>0x12</code>\uFF0C\u63A5\u7740\u662F<code>0x34</code>\uFF0C\u7136\u540E\u662F<code>0x56</code>\uFF0C\u6700\u540E\u662F<code>0x78</code>\u3002</p><h2 id="\u5B57\u8282\u5E8F\u5728socket\u4E2D\u7684\u4F8B\u5B50" tabindex="-1"><a class="header-anchor" href="#\u5B57\u8282\u5E8F\u5728socket\u4E2D\u7684\u4F8B\u5B50" aria-hidden="true">#</a> \u5B57\u8282\u5E8F\u5728socket\u4E2D\u7684\u4F8B\u5B50</h2><p>\u5F53\u4F60\u5728\u7F51\u7EDC\u5C01\u5305\u6216\u8005\u5728\u586B\u5145\u6570\u636E\u7684\u65F6\u5019\uFF0C\u4F60\u9700\u8981\u786E\u5B9A\u4F60\u7684\u7AEF\u53E3\u53F7\uFF08port number\uFF09\u548Cip\u5730\u5740\u90FD\u662F\u7B26\u5408\u7F51\u7EDC\u5B57\u8282\u5E8F\u7684\u3002\u4F46\u662F\u4F60\u5E76\u4E0D\u77E5\u9053\u4F60\u7684\u4E3B\u673A\u5B57\u8282\u5E8F\u662F\u5927\u7AEF\u8FD8\u662F\u5C0F\u7AEF\uFF0C\u4F60\u81EA\u7136\u4E5F\u5C31\u641E\u4E0D\u6E05\u4F60\u5230\u5E95\u9700\u4E0D\u9700\u8981\u8FDB\u884C\u8F6C\u6362\u3002</p><p>\u5176\u5B9E\u4E0D\u5FC5\u5982\u6B64\u8D39\u795E\uFF0C\u4E0D\u7528\u5728\u610F\u4E3B\u673A\u5E8F\uFF0C\u76F4\u63A5\u8C03\u7528\u51FD\u6570\u5F3A\u884C\u5C06\u4E3B\u673A\u5E8F\u8F6C\u6362\u4E3A\u7F51\u7EDC\u5B57\u8282\u5E8F\u5373\u53EF\u3002\u4E0B\u9762\u4E3E\u4E00\u4E2AC\u8BED\u8A00\u7F16\u5199\u670D\u52A1\u5668\u4EE3\u7801\u7ED1\u5B9A\u7AEF\u53E3\u7684\u4F8B\u5B50\u3002</p><p><img src="http://qiniu.chanmufeng.com/2022-10-06-053355.png" alt="image-20221006133355026" loading="lazy"></p><p>\u4EE3\u7801\u4E2D\u88AB\u7EA2\u8272\u6807\u8BB0\u7684\u90E8\u5206\u5C31\u662F\u5206\u522B\u5C06\u7AEF\u53E3\u53F7<code>33000</code>\u7531\u4E3B\u673A\u5E8F\u8F6C\u6362\u4E3A\u7F51\u7EDC\u5B57\u8282\u5E8F\uFF0C\u5C06ip\u5730\u5740\u7531\u4E3B\u673A\u5E8F\u8F6C\u6362\u4E3A\u7F51\u7EDC\u5B57\u8282\u5E8F\u7684\u51FD\u6570\u3002</p><p>\u4E4D\u773C\u4E00\u770B\uFF0C\u51FD\u6570\u540D\u975E\u5E38\u53E4\u602A\uFF0C\u4E0D\u597D\u8BB0\u5FC6\uFF0C\u4E0B\u9762\u7A0D\u5FAE\u89E3\u91CA\u4E00\u4E0B\uFF0C\u5404\u4F4D\u5C31\u8C41\u7136\u5F00\u6717\u4E86\u3002</p><p><code>Socket\u51FD\u6570\u5E93</code>\u63D0\u4F9B\u4E86\u8FD9\u79CD\u5B57\u8282\u5E8F\u8F6C\u6362\u7684\u51FD\u6570\uFF0C\u51FD\u6570\u7684\u4F5C\u7528\u5BF9\u8C61\u6709\u4E24\u79CD\uFF0C\u5206\u522B\u662F<code>short</code>\uFF082\u5B57\u8282\uFF09\u548C<code>long</code>\uFF084\u5B57\u8282\uFF09\uFF0C\u8FD9\u5C31\u662F\u51FD\u6570\u6700\u540E\u7684\u300C<code>s</code>\u300D\u548C\u300C<code>l</code>\u300D\u7684\u542B\u4E49\u3002</p><p>\u8FD8\u6709\uFF0C\u4E3B\u673A\u5B57\u8282\u5E8F\u7684\u82F1\u6587\u662F<code>Host Byte Order</code>\uFF0C\u7B80\u5199\u4E3A\u300C<code>h</code>\u300D\uFF0C\u7F51\u7EDC\u5B57\u8282\u5E8F\u7684\u82F1\u6587\u662F<code>Network Byte Order</code>\uFF0C\u7B80\u5199\u4E3A\u300C<code>n</code>\u300D\uFF0C\u300C<code>to</code>\u300D\u8868\u793A\u8F6C\u6362\uFF0C\u6240\u4EE5\uFF0C\u5982\u679C\u4F60\u60F3\u5C06<code>short</code>\u6570\u636E\u4ECE\u4E3B\u673A\u5B57\u8282\u5E8F\u8F6C\u6362\u4E3A\u7F51\u7EDC\u5B57\u8282\u5E8F\uFF0C\u90A3\u5C31\u5E94\u8BE5\u662F<code>h-to-n-s</code>\uFF0C\u8868\u793A<code>Host to Network Short</code>\u3002\u662F\u4E0D\u662F\u5F88\u7B80\u5355\uFF1F</p><p>\u6240\u4EE5\u7406\u8BBA\u4E0A\uFF0C\u4F60\u53EF\u4EE5\u7528\u300C<code>h</code>\u300D\u3001\u300C<code>to</code>\u300D\u3001\u300C<code>n</code>\u300D\u3001\u300C<code>s/l</code>\u300D\u8FD9\u51E0\u4E2A\u5B57\u6BCD\u7075\u6D3B\u7EC4\u5408\uFF0C\u7EC4\u6210\u4F60\u60F3\u8981\u7684api\uFF0C\u4F46\u662F\u4E5F\u522B\u778E\u641E\uFF0Cstolh()\uFF3B&quot;Short to Long Host&quot;\uFF3D\u8FD9\u79CD\u7EC4\u5408\u538B\u6839\u90FD\u4E0D\u5B58\u5728\u3002</p><p>\u57FA\u672C\u4E0A\u4F60\u9700\u8981\u7684\u4E5F\u5C31\u662F\u4EE5\u4E0B\u56DB\u79CD\u7F62\u4E86\uFF1A</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>htons() // host to network short
htonl() // host to network long
ntohs() // network to host short
ntohl() // network to host long
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\u57FA\u672C\u4E0A\uFF0C\u6211\u4EEC\u5728\u8FDB\u884Csocket\u7F16\u7A0B\u65F6\uFF0C\u5728\u53D1\u9001\u6570\u636E\u4E4B\u524D\u90FD\u9700\u8981\u628A\u6570\u636E\u8F6C\u6362\u4E3A\u7F51\u7EDC\u5B57\u8282\u5E8F\uFF0C\u5E76\u5728\u6536\u5230\u6570\u636E\u4E4B\u540E\u518D\u8F6C\u6362\u6210\u4E3B\u673A\u5B57\u8282\u5E8F\u3002</p><blockquote><p>\u66F4\u591A\u5173\u4E8E\u6D6E\u70B9\u6570\u7684\u5904\u7406\uFF0C\u4F1A\u5728\u4E4B\u540E\u4E13\u95E8\u7684\u7AE0\u8282\u63D0\u53CA\u3002</p></blockquote><blockquote><p>\u5982\u65E0\u7279\u6B8A\u8BF4\u660E\uFF0C\u672C\u5C0F\u518C\u7684\u6570\u503C\u9884\u8BBE\u90FD\u89C6\u4E3A\u4E3B\u673A\u5B57\u8282\u5E8F\u3002</p></blockquote>`,44),r=[p];function n(a,s){return o(),d("div",null,r)}var h=e(t,[["render",n],["__file","\u5B57\u8282\u5E8F.html.vue"]]);export{h as default};
